var mapdataLength = 20;
var imagedata  = {"0":{
     src:"diagram-overview-1160x900.bmp",
     wightBase:600,
     heightBase:470,
     usemap:"#custom-image-map",
     wight:900,
     height:705
}};

export default {
  name: 'mapArchitecture',
  data: function () {
    return { 
      mapdata: mapdata,
      imagedata: imagedata
    }
  },
  mounted() {
    this.scaleImage();
  },
  methods: {
    scaleImage(){
      var factor = (parseFloat(imagedata[0].height)/parseFloat(imagedata[0].heightBase));
      var i = 0;
      
      // extract coords
      for (i = 0; i < mapdataLength ; i++){
        var list = mapdata[i].coords.split(',');
        var temp = mapdata[i];
        var coords = "";
        var j = 0;
        var k = 0;
        
        // scale coords
        for (j = 0; j < 8 ; j++){  
          list[j] = Math.round(parseFloat(list[j])*factor);
        }

        // build coords
        for (k = 0; k < 8 ; k++){
          if(k==0){
            coords = ""+parseInt(list[k])+"";
          } else {
            coords = coords + ","+ parseInt(list[k]);
          }
        }
        
        // build mapdata
        mapdata[i] = { target:temp.target,
                       alt:temp.alt,
                       title:temp.title,
                       href:temp.href,
                       coords:coords,
                       shape:temp.shape
                     };
      }
    }
  }
